long long number(char *s) {
	char* pszStart = s;
	if (*s == '\\') {
		s++;
	}
	int nBase = 10;
	if (*s == '0') {
		if (s[1] == 'x' || s[1] == 'X') {
			s += 2;
			nBase = 16;
		} else if (s[1] == 'b') {
			s += 2;
			nBase = 2;
		} else {
			s++;
			nBase = 8;
		}
	} 
	long long i = 0;
	int o;
	for (;;) {
		if (*s == '_') {
			s++;
			continue;
		}
		if (*s >= '0' && *s <= '9') {
			o = (*s - '0');
		} else if (nBase == 16) {
			if (*s >= 'A' && *s <= 'F') {
				o = (*s - 'A' + 10);
			} else if (*s >= 'a' && *s <= 'f') {
				o = (*s - 'a' + 10);
			} else {
				break;
			}
		} else {
			break;
		}
		i *= nBase;
		i += o;
		if (i < 0) {
			yyerror("Integer overflow while converting %s", pszStart);
			break;
		}
		s++;
	}
	return i;
}

